import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm: FormGroup;
  registerForm: FormGroup;
  isLoginMode = true;
  error = '';
  success = '';
  loading = false;

  constructor(
    private formBuilder: FormBuilder,
    private authService: AuthService,
    private router: Router
  ) {
    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });

    this.registerForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ['', [Validators.required]]
    });
  }

  onLogin(): void {
    if (this.loginForm.invalid) {
      return;
    }

    this.loading = true;
    this.error = '';

    const { email, password } = this.loginForm.value;

    this.authService.login(email, password).subscribe({
      next: () => {
        this.loading = false;
        this.router.navigate(['/dashboard']);
      },
      error: (err) => {
        this.loading = false;
        this.error = err.error?.error || 'Login failed';
      }
    });
  }

  onRegister(): void {
    if (this.registerForm.invalid) {
      return;
    }

    const { email, password, confirmPassword } = this.registerForm.value;

    if (password !== confirmPassword) {
      this.error = 'Passwords do not match';
      return;
    }

    this.loading = true;
    this.error = '';
    this.success = '';

    this.authService.register(email, password).subscribe({
      next: () => {
        this.loading = false;
        this.success = 'Registration successful! Please login.';
        this.isLoginMode = true;
        this.registerForm.reset();
      },
      error: (err) => {
        this.loading = false;
        this.error = err.error?.error || 'Registration failed';
      }
    });
  }

  toggleMode(): void {
    this.isLoginMode = !this.isLoginMode;
    this.error = '';
    this.success = '';
  }
}
<div class="container" style="max-width: 500px; margin-top: 50px;">
  <div class="card">
    <h2>{{ isLoginMode ? 'Login' : 'Register' }}</h2>

    <div *ngIf="error" class="alert alert-error">{{ error }}</div>
    <div *ngIf="success" class="alert alert-success">{{ success }}</div>

    <form *ngIf="isLoginMode" [formGroup]="loginForm" (ngSubmit)="onLogin()">
      <div class="form-group">
        <label>Email</label>
        <input type="email" class="form-control" formControlName="email" placeholder="Enter email">
      </div>

      <div class="form-group">
        <label>Password</label>
        <input type="password" class="form-control" formControlName="password" placeholder="Enter password">
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="loading || loginForm.invalid">
        {{ loading ? 'Loading...' : 'Login' }}
      </button>
    </form>

    <form *ngIf="!isLoginMode" [formGroup]="registerForm" (ngSubmit)="onRegister()">
      <div class="form-group">
        <label>Email</label>
        <input type="email" class="form-control" formControlName="email" placeholder="Enter email">
      </div>

      <div class="form-group">
        <label>Password</label>
        <input type="password" class="form-control" formControlName="password" placeholder="Enter password">
      </div>

      <div class="form-group">
        <label>Confirm Password</label>
        <input type="password" class="form-control" formControlName="confirmPassword" placeholder="Confirm password">
      </div>

      <button type="submit" class="btn btn-primary" [disabled]="loading || registerForm.invalid">
        {{ loading ? 'Loading...' : 'Register' }}
      </button>
    </form>

    <p style="margin-top: 20px; text-align: center;">
      {{ isLoginMode ? "Don't have an account?" : 'Already have an account?' }}
      <a href="#" (click)="toggleMode(); $event.preventDefault()">
        {{ isLoginMode ? 'Register' : 'Login' }}
      </a>
    </p>
  </div>
</div>
