import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
}
<div class="container">
  <h1>Dashboard</h1>
  <div class="card">
    <h2>Welcome to Product Management System</h2>
    <p>Use the navigation menu to manage:</p>
    <ul>
      <li><strong>Users</strong> - Create and update user accounts</li>
      <li><strong>Categories</strong> - Manage product categories</li>
      <li><strong>Products</strong> - Manage products with bulk upload and report generation</li>
    </ul>
  </div>
</div>
