import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ProductService } from '../../services/product.service';
import { CategoryService } from '../../services/category.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit {
  products: any[] = [];
  categories: any[] = [];
  loading = false;
  error = '';
  success = '';
  showModal = false;
  showBulkUploadModal = false;
  productForm: FormGroup;
  editingProduct: any = null;
  selectedFile: File | null = null;
  bulkUploadFile: File | null = null;
  bulkUploadProgress = '';

  // Pagination
  currentPage = 1;
  pageSize = 10;
  totalItems = 0;
  totalPages = 0;

  // Filters
  searchTerm = '';
  categoryFilter = '';
  sortBy = 'createdAt';
  sortOrder = 'desc';

  constructor(
    private productService: ProductService,
    private categoryService: CategoryService,
    private formBuilder: FormBuilder
  ) {
    this.productForm = this.formBuilder.group({
      name: ['', Validators.required],
      price: ['', [Validators.required, Validators.min(0)]],
      categoryId: ['', Validators.required],
      image: ['']
    });
  }

  ngOnInit(): void {
    this.loadCategories();
    this.loadProducts();
  }

  loadCategories(): void {
    this.categoryService.getAllCategories().subscribe({
      next: (response) => {
        this.categories = response.data;
      },
      error: (err) => {
        console.error('Failed to load categories', err);
      }
    });
  }

  loadProducts(): void {
    this.loading = true;
    const params = {
      page: this.currentPage,
      limit: this.pageSize,
      search: this.searchTerm,
      categoryId: this.categoryFilter,
      sortBy: this.sortBy,
      sortOrder: this.sortOrder
    };

    this.productService.getAllProducts(params).subscribe({
      next: (response) => {
        this.products = response.data;
        this.totalItems = response.pagination.total;
        this.totalPages = response.pagination.totalPages;
        this.loading = false;
      },
      error: (err) => {
        this.error = err.error?.error || 'Failed to load products';
        this.loading = false;
      }
    });
  }

  onSearch(): void {
    this.currentPage = 1;
    this.loadProducts();
  }

  onSort(field: string): void {
    if (this.sortBy === field) {
      this.sortOrder = this.sortOrder === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortBy = field;
      this.sortOrder = 'asc';
    }
    this.loadProducts();
  }

  onPageChange(page: number): void {
    this.currentPage = page;
    this.loadProducts();
  }

  openModal(product?: any): void {
    this.editingProduct = product || null;
    if (product) {
      this.productForm.patchValue({
        name: product.name,
        price: product.price,
        categoryId: product.categoryId
      });
    } else {
      this.productForm.reset();
    }
    this.selectedFile = null;
    this.showModal = true;
    this.error = '';
    this.success = '';
  }

  closeModal(): void {
    this.showModal = false;
    this.editingProduct = null;
    this.productForm.reset();
    this.selectedFile = null;
  }

  onFileSelected(event: any): void {
    const file = event.target.files[0];
    if (file) {
      this.selectedFile = file;
    }
  }

  onSubmit(): void {
    if (this.productForm.invalid) return;

    const formData = new FormData();
    formData.append('name', this.productForm.value.name);
    formData.append('price', this.productForm.value.price);
    formData.append('categoryId', this.productForm.value.categoryId);

    if (this.selectedFile) {
      formData.append('image', this.selectedFile);
    }

    if (this.editingProduct) {
      this.productService.updateProduct(this.editingProduct.id, formData).subscribe({
        next: () => {
          this.success = 'Product updated successfully';
          this.closeModal();
          this.loadProducts();
        },
        error: (err) => {
          this.error = err.error?.error || 'Failed to update product';
        }
      });
    } else {
      this.productService.createProduct(formData).subscribe({
        next: () => {
          this.success = 'Product created successfully';
          this.closeModal();
          this.loadProducts();
        },
        error: (err) => {
          this.error = err.error?.error || 'Failed to create product';
        }
      });
    }
  }

  deleteProduct(id: number): void {
    if (confirm('Are you sure you want to delete this product?')) {
      this.productService.deleteProduct(id).subscribe({
        next: () => {
          this.success = 'Product deleted successfully';
          this.loadProducts();
        },
        error: (err) => {
          this.error = err.error?.error || 'Failed to delete product';
        }
      });
    }
  }

  openBulkUploadModal(): void {
    this.showBulkUploadModal = true;
    this.bulkUploadFile = null;
    this.bulkUploadProgress = '';
    this.error = '';
  }

  closeBulkUploadModal(): void {
    this.showBulkUploadModal = false;
    this.bulkUploadFile = null;
    this.bulkUploadProgress = '';
  }

  onBulkFileSelected(event: any): void {
    const file = event.target.files[0];
    if (file) {
      this.bulkUploadFile = file;
    }
  }

  onBulkUpload(): void {
    if (!this.bulkUploadFile) {
      this.error = 'Please select a file';
      return;
    }

    this.bulkUploadProgress = 'Uploading...';
    this.productService.bulkUpload(this.bulkUploadFile).subscribe({
      next: (response) => {
        this.bulkUploadProgress = `Upload complete! Success: ${response.summary.success}, Failed: ${response.summary.failed}`;
        this.success = 'Bulk upload completed';
        this.loadProducts();
        setTimeout(() => this.closeBulkUploadModal(), 3000);
      },
      error: (err) => {
        this.error = err.error?.error || 'Bulk upload failed';
        this.bulkUploadProgress = '';
      }
    });
  }

  downloadReport(format: string): void {
    this.productService.generateReport(format).subscribe({
      next: (blob) => {
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `products-report.${format}`;
        link.click();
        window.URL.revokeObjectURL(url);
        this.success = 'Report downloaded successfully';
      },
      error: (err) => {
        this.error = 'Failed to generate report';
      }
    });
  }

  getCategoryName(categoryId: number): string {
    const category = this.categories.find(c => c.id === categoryId);
    return category ? category.name : 'Unknown';
  }
}
<div class="container">
  <h1>Product Management</h1>

  <div *ngIf="error" class="alert alert-error">{{ error }}</div>
  <div *ngIf="success" class="alert alert-success">{{ success }}</div>

  <div style="display: flex; gap: 10px; margin-bottom: 20px;">
    <button class="btn btn-primary" (click)="openModal()">Add Product</button>
    <button class="btn btn-success" (click)="openBulkUploadModal()">Bulk Upload</button>
    <button class="btn btn-secondary" (click)="downloadReport('csv')">Download CSV</button>
    <button class="btn btn-secondary" (click)="downloadReport('xlsx')">Download XLSX</button>
  </div>

  <div class="search-bar">
    <input 
      type="text" 
      class="form-control" 
      [(ngModel)]="searchTerm" 
      (keyup.enter)="onSearch()"
      placeholder="Search products...">

    <select class="form-control" [(ngModel)]="categoryFilter" (change)="onSearch()">
      <option value="">All Categories</option>
      <option *ngFor="let category of categories" [value]="category.id">
        {{ category.name }}
      </option>
    </select>

    <button class="btn btn-primary" (click)="onSearch()">Search</button>
  </div>

  <div *ngIf="loading" class="loading">Loading...</div>

  <div *ngIf="!loading" class="card">
    <table class="table">
      <thead>
        <tr>
          <th>ID</th>
          <th>Unique ID</th>
          <th>Name</th>
          <th (click)="onSort('price')" style="cursor: pointer;">
            Price {{ sortBy === 'price' ? (sortOrder === 'asc' ? '↑' : '↓') : '' }}
          </th>
          <th>Category</th>
          <th>Image</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let product of products">
          <td>{{ product.id }}</td>
          <td>{{ product.uniqueId }}</td>
          <td>{{ product.name }}</td>
          <td>${{ product.price }}</td>
          <td>{{ product.category?.name }}</td>
          <td>
            <img *ngIf="product.image" [src]="'http://localhost:3000/uploads/' + product.image" 
                 alt="{{ product.name }}" style="width: 50px; height: 50px; object-fit: cover;">
            <span *ngIf="!product.image">No image</span>
          </td>
          <td>
            <div class="actions">
              <button class="btn btn-primary" (click)="openModal(product)">Edit</button>
              <button class="btn btn-danger" (click)="deleteProduct(product.id)">Delete</button>
            </div>
          </td>
        </tr>
      </tbody>
    </table>

    <div class="pagination" *ngIf="totalPages > 1">
      <button (click)="onPageChange(currentPage - 1)" [disabled]="currentPage === 1">
        Previous
      </button>
      <span>Page {{ currentPage }} of {{ totalPages }}</span>
      <button (click)="onPageChange(currentPage + 1)" [disabled]="currentPage === totalPages">
        Next
      </button>
    </div>
  </div>
</div>

<!-- Product Modal -->
<div class="modal-overlay" *ngIf="showModal" (click)="closeModal()">
  <div class="modal" (click)="$event.stopPropagation()">
    <h2>{{ editingProduct ? 'Edit Product' : 'Add Product' }}</h2>

    <form [formGroup]="productForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label>Product Name</label>
        <input type="text" class="form-control" formControlName="name" placeholder="Enter product name">
      </div>

      <div class="form-group">
        <label>Price</label>
        <input type="number" class="form-control" formControlName="price" placeholder="Enter price" step="0.01">
      </div>

      <div class="form-group">
        <label>Category</label>
        <select class="form-control" formControlName="categoryId">
          <option value="">Select category</option>
          <option *ngFor="let category of categories" [value]="category.id">
            {{ category.name }}
          </option>
        </select>
      </div>

      <div class="form-group">
        <label>Image</label>
        <input type="file" class="form-control" (change)="onFileSelected($event)" accept="image/*">
      </div>

      <div class="modal-actions">
        <button type="button" class="btn btn-secondary" (click)="closeModal()">Cancel</button>
        <button type="submit" class="btn btn-primary" [disabled]="productForm.invalid">
          {{ editingProduct ? 'Update' : 'Create' }}
        </button>
      </div>
    </form>
  </div>
</div>

<!-- Bulk Upload Modal -->
<div class="modal-overlay" *ngIf="showBulkUploadModal" (click)="closeBulkUploadModal()">
  <div class="modal" (click)="$event.stopPropagation()">
    <h2>Bulk Upload Products</h2>

    <div class="alert alert-info">
      <p><strong>Instructions:</strong></p>
      <ul>
        <li>Upload a CSV or XLSX file</li>
        <li>Required columns: name, price, categoryId</li>
        <li>Optional column: image</li>
      </ul>
    </div>

    <div class="form-group">
      <label>Select File</label>
      <input type="file" class="form-control" (change)="onBulkFileSelected($event)" 
             accept=".csv,.xlsx,.xls">
    </div>

    <div *ngIf="bulkUploadProgress" class="alert alert-info">
      {{ bulkUploadProgress }}
    </div>

    <div class="modal-actions">
      <button type="button" class="btn btn-secondary" (click)="closeBulkUploadModal()">Cancel</button>
      <button type="button" class="btn btn-primary" (click)="onBulkUpload()" 
              [disabled]="!bulkUploadFile || bulkUploadProgress">
        Upload
      </button>
    </div>
  </div>
</div>
